import{openDB}from"idb";import CONFIG from"../../config/config";const{DATABASE_NAME,OBJECT_STORE_NAME,DATABASE_VERSION}=CONFIG,dbPromise=openDB(DATABASE_NAME,DATABASE_VERSION,{upgrade(t){t.createObjectStore(OBJECT_STORE_NAME,{keyPath:"id"})}}),FavoriteRestaurantIdb={getFavoriteRestaurant:async t=>t?(await dbPromise).get(OBJECT_STORE_NAME,t):console.log("no id"),getAllRestaurants:async()=>(await dbPromise).getAll(OBJECT_STORE_NAME),addRestaurant:async t=>(await dbPromise).put(OBJECT_STORE_NAME,t),putRestaurant:async t=>(await dbPromise).put(OBJECT_STORE_NAME,t),deleteRestaurant:async t=>(await dbPromise).delete(OBJECT_STORE_NAME,t)};export default FavoriteRestaurantIdb;